// script.js

document.addEventListener('DOMContentLoaded', function() {
    const header = document.querySelector('.App-header');
    const scrollTopBtn = document.getElementById('scrollTop');

    // Função para adicionar ou remover a classe 'scrolled' no cabeçalho
    const toggleHeaderClass = () => {
        if (window.scrollY > 50) {
            header.classList.add('scrolled');
        } else {
            header.classList.remove('scrolled');
        }
    };

    // Função para mostrar ou esconder o botão de scrollTop
    const toggleScrollTopBtn = () => {
        if (window.scrollY >= 560) {
            scrollTopBtn.classList.add("show-scroll");
        } else {
            scrollTopBtn.classList.remove("show-scroll");
        }
    };

    // Listener de rolagem para cabeçalho e scrollTop
    window.addEventListener('scroll', function() {
        toggleHeaderClass();
        toggleScrollTopBtn();
    });

    const swiper = new Swiper('.swiper', {
        speed: 400,
        spaceBetween: 100,
        pagination: {
            el: '.swiper-pagination',
            clickable: true,
        },
        navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
        },
        scrollbar: {
            el: '.swiper-scrollbar',
        },
        effect: 'fade',
        fadeEffect: {
            crossFade: true,
        },
    });

    // Salvando IPs dos Visitantes
    fetch('https://api.ipify.org?format=json')
    .then(response => response.json())
    .then(data => {
        fetch('https://seuservidor.com/salvar-ip', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ ip: data.ip })
        });
    });
});

